import React from "react";
import { Route, Routes, Link } from "react-router-dom";
import "bootstrap/dist/css/bootstrap.min.css";

import AddReview from "./components/add-review";
import Restaurant from "./components/restaurants";
import RestaurantsList from "./components/restaurants-list";
import Login from "./components/login";

function App() {
  // React Hook
  const [user, setUser] = React.useState(null);

  async function login(user = null) {
    setUser(user);
  }

  async function logout() {
    setUser(null);
  }

  return (
    <div>
      {/* Navigation Bar (Copy and Pasted From bootstrap) */}
      <nav className="navbar navbar-expand navbar-dark bg-dark">
        {/* a tag is a hyperlink, href is what it references */}
        <a href="/restaurants" className="navbar-brand">
          Restaurant Reviews
        </a>
        <div className="navbar-nav mr-auto">
          {/* li tag is for lists */}
          <li className="nav-item">
            {/* Link will change ROUTES to the specified one */}
            <Link to={"/restaurants"} className="nav-link">
              Restaurants
            </Link>
          </li>
          {/* This will look different depending if the user exist (is logged in) */}
          <li className="nav-item">
            {/* Curly braces mean, put in some code. I.e. not literal */}
            { user ? (
              // if user, display "Logout" + username, and run logout function on click
              <a onClick={logout} className="nav-link" style = {{cursor:'pointer'}}>
                Logout {user.name}
              </a>
            ) : (
              // if not user, link to login Page
              <Link to={"/login"} className="nav-link">
                Login
              </Link>
            )}
          </li>
        </div>
      </nav>

      <div className="container mt-3">
        <Routes>
          <Route path={["/", "/restaurants"]} element={RestaurantsList}/>
          <Route
            path="/restaurants/:id/review"
            render={(props) => (
              <AddReview {...props} user={user} />
            )}
          />
          <Route
            path = "/login"
            render={(props) => (
              <Login {...props} login={login} />
            )}
          />
        </Routes>
      </div>

    </div>

  );
}

export default App;
